            <button>{/* #4 Sort button */}</button>
            <button>{/* #5 Reset button */}</button>
          </section>
          <ListItems items={data}></ListItems>
        </div>
        <div class="info-area">{/* #3 display selectedCountryHolidays here */}</div>
